<?php

declare(strict_types=1);

namespace App\Http\Middleware;

use Laminas\Diactoros\Response;
use Mikrofraim\Http\Middleware;
use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Psr\Http\Server\RequestHandlerInterface;

class Favicon extends Middleware
{
    public const FAVICON_DATA = 'AAABAAEAEBAAAAEAIABoBAAAFgAAACgAAAAQAAAAIAAAAAEAIAAAA'
                       . 'AAAAAQAAAAAAAAAAAAAAAAAAAAAAAAAAAD/AAAA/wAAAP8AAAD/AA'
                       . 'AA/wAAAP8AAAD/AAAA/wAAAP8AAAD/AAAA/wAAAP8AAAD/AAAA/wA'
                       . '';

    public function process(
        ServerRequestInterface $request,
        RequestHandlerInterface $handler,
    ): ResponseInterface {
        if ($request->getUri()->getPath() === '/favicon.ico') {
            $response = new Response();

            $response = $response->withHeader('Content-Type', 'file/x-icon');
            $response = $response->withHeader(
                'Content-Length',
                \mb_strlen(\base64_decode(self::FAVICON_DATA, true)),
            );

            $response->getBody()->write(\base64_decode(self::FAVICON_DATA, true));

            return $response;
        }

        return $handler->handle($request);
    }
}
